    background-color: #fff;
    right: 0;
    bottom: 0;
    border-radius: 16px 0px 0px 0px;
    display: flex;

    & a i {
      margin: 20px 0 0 22px;
      padding: 12px;
      border: 2px solid #d7d7d7;
      border-radius: 100%;
      color: #d7d7d7;
      display: block;

      &:hover {
        transition: all ease-in-out 0.3s;
        color: #000;
        border: 2px solid #000;
      }
    }
  }
  &__plus {
    position: absolute;
    left: -15px;
    bottom: -15px;
  }
  &__circle {
    position: absolute;
    right: -20px;
    top: -15px;
    z-index: -1;
  }
}

.story {
  margin-top: 215px;

  &__header {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
  }

  &__title {
    font-size: 36px;
  }

  &__link {
    font-size: 17px;
    font-weight: 600;
    color: $orange;
  }

  &__row {
    display: flex;
    padding-top: 35px;
    margin: 0 -12.5px;
  }

  &__column {
    display: flex;
    flex: 0 1 25%;
    padding: 0 12.5px;
  }

  &__item {
    display: flex;
    flex-direction: column;
  }

  &__image {
    height: 245px;
    img {
      border-radius: 8px 8px 0 0;
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
  }

  &__subtitle {
    font-weight: bold;
    font-size: 19px;
    color: #000000;
    padding-top: 10px;
    line-height: 30px;
  }

  &__text {
    flex: 1 1 auto;
    padding: 15px 0 15px 0;
    width: 75%;
    p {
      font-size: 15px;
      color: #7d7987;
      line-height: 26px;
    }
  }

  &__link {
    a {
      color: $orange;
      font-size: 15px;
    }
  }
}

#footer {
  background: #f9f9fb;
  margin-top: 270px;
}

.footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  padding: 70px 0 70px 0;

  &__info {
    width: 50%;
  }

  &__text {
    font-family: Mulish;
    font-weight: 300;
    font-size: 18px;
    line-height: 28px;
    color: #848484;
    padding: 25px 0 55px 0;
    width: 50%;
  }

  &__reserved {
    font-family: Mulish;
    font-style: normal;
    font-weight: 300;
    font-size: 17px;
    line-height: 28px;
    color: #848484;
  }

  &__row {
    display: flex;
    justify-content: space-between;
    width: 50%;
  }

  &__subtitle {
    font-family: Mulish;
    font-weight: bold;
    font-size: 19px;
    line-height: 60px;
  }

  &__points {
    li {
      font-family: Mulish;
      font-weight: 300;
      font-size: 18px;
      line-height: 38px;
      color: #848484;
      cursor: pointer;
    }
  }
}

.social {
  display: flex;
  justify-content: center;
  align-items: center;
  border-top: 1px solid #c4c4c4;
  &__row {
    padding: 32px 0 32px 0;
    a i {
      color: #000;
      padding-left: 35px;
      font-size: 22px;
    }
  }
}
